import React, { useState, useRef } from 'react';
import PlayerControls from './PlayerControls';

const DocumentViewer = ({ documentData }) => {
  const [currentPage, setCurrentPage] = useState(0);
  const [highlightedWord, setHighlightedWord] = useState(null);
  const stageRef = useRef(null);
  
  const pageData = documentData.pages[currentPage] || {};
  const { words = [], dimensions = [800, 600], image } = pageData;

  const handlePrevious = () => {
    setCurrentPage(prev => Math.max(0, prev - 1));
    setHighlightedWord(null);
  };

  const handleNext = () => {
    setCurrentPage(prev => Math.min(documentData.pages.length - 1, prev + 1));
    setHighlightedWord(null);
  };

  const highlightWord = (word, index) => {
    setHighlightedWord({ ...word, index });
  };

  return (
    <div className="document-viewer">
      <div className="controls">
        <button 
          className="btn"
          onClick={handlePrevious}
          disabled={currentPage === 0}
        >
          ← Previous
        </button>
        
        <span>Page {currentPage + 1} of {documentData.pages.length}</span>
        
        <button 
          className="btn"
          onClick={handleNext}
          disabled={currentPage === documentData.pages.length - 1}
        >
          Next →
        </button>
      </div>

      <div className="viewport-container">
        {image ? (
          <div style={{ position: 'relative' }}>
            <img 
              src={image} 
              alt={`Page ${currentPage + 1}`} 
              style={{ 
                maxWidth: '100%', 
                height: 'auto',
                display: 'block',
                borderRadius: '8px',
                boxShadow: '0 4px 20px rgba(0,0,0,0.1)'
              }}
            />
            
            {highlightedWord && highlightedWord.bbox && (
              <div 
                className="highlight-overlay"
                style={{
                  position: 'absolute',
                  left: `${highlightedWord.bbox[0]}px`,
                  top: `${highlightedWord.bbox[1]}px`,
                  width: `${highlightedWord.bbox[2] - highlightedWord.bbox[0]}px`,
                  height: `${highlightedWord.bbox[3] - highlightedWord.bbox[1]}px`,
                  backgroundColor: 'rgba(255, 230, 0, 0.3)',
                  border: '2px solid #FFD700',
                  borderRadius: '3px',
                  pointerEvents: 'none'
                }}
              />
            )}
          </div>
        ) : (
          <div className="text-content">
            <pre style={{ 
              padding: '20px', 
              whiteSpace: 'pre-wrap',
              fontFamily: 'inherit',
              minHeight: '500px',
              lineHeight: '1.8',
              fontSize: '1.1rem',
              backgroundColor: '#fff',
              borderRadius: '8px',
              boxShadow: '0 4px 20px rgba(0,0,0,0.05)'
            }}>
              {pageData.text}
            </pre>
          </div>
        )}
      </div>

      <PlayerControls 
        words={words} 
        onHighlight={highlightWord}
        highlightedIndex={highlightedWord?.index}
      />
    </div>
  );
};

export default DocumentViewer;
