import React, { useState, useEffect, useRef } from 'react';

const PlayerControls = ({ words, onHighlight, highlightedIndex }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const progressRef = useRef(null);

  useEffect(() => {
    if (isPlaying) {
      const interval = setInterval(() => {
        setCurrentIndex(prev => {
          if (prev >= words.length - 1) {
            setIsPlaying(false);
            return 0;
          }
          return prev + 1;
        });
      }, 1000);
      
      return () => clearInterval(interval);
    }
  }, [isPlaying, words]);

  useEffect(() => {
    if (words.length > 0 && currentIndex < words.length) {
      onHighlight(words[currentIndex], currentIndex);
      
      // Scroll to highlighted word
      if (progressRef.current) {
        const wordElement = progressRef.current.children[currentIndex];
        if (wordElement) {
          wordElement.scrollIntoView({ 
            behavior: 'smooth', 
            block: 'nearest', 
            inline: 'center' 
          });
        }
      }
    }
  }, [currentIndex, words, onHighlight]);

  const togglePlayback = () => {
    setIsPlaying(!isPlaying);
  };

  const handleWordClick = (word, index) => {
    setCurrentIndex(index);
    onHighlight(word, index);
  };

  return (
    <div className="player-controls">
      <div className="playback-controls">
        <button 
          className="play-btn"
          onClick={togglePlayback}
          disabled={words.length === 0}
        >
          {isPlaying ? (
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <rect x="6" y="4" width="4" height="16"></rect>
              <rect x="14" y="4" width="4" height="16"></rect>
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <polygon points="5 3 19 12 5 21 5 3"></polygon>
            </svg>
          )}
        </button>
        <div className="progress-bar">
          <div 
            className="progress" 
            style={{ width: `${(currentIndex / Math.max(1, words.length - 1)) * 100}%` }}
          ></div>
        </div>
        <div className="time">
          {Math.floor(currentIndex / 60)}:{String(currentIndex % 60).padStart(2, '0')} / 
          {Math.floor(words.length / 60)}:{String(words.length % 60).padStart(2, '0')}
        </div>
      </div>

      {words.length > 0 && (
        <div className="progress-container" ref={progressRef}>
          <div className="progress-text">
            {words.map((word, index) => (
              <span 
                key={index}
                className={`word ${index === currentIndex ? 'active' : ''} ${index === highlightedIndex ? 'highlighted' : ''}`}
                onClick={() => handleWordClick(word, index)}
              >
                {word.text}
              </span>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PlayerControls;
