import React, { useState, useEffect, useRef } from 'react';

const PlayerControls = ({ words, onHighlight, highlightedIndex }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const utteranceRef = useRef(null);
  const synthRef = useRef(null);
  const isMountedRef = useRef(true);
  const lastSpokenIndexRef = useRef(0);

  // Initialize speech synthesis
  useEffect(() => {
    isMountedRef.current = true;
    
    if (!window.speechSynthesis) {
      console.warn("Web Speech API not supported");
      return;
    }

    synthRef.current = window.speechSynthesis;
    
    return () => {
      isMountedRef.current = false;
      if (synthRef.current) {
        synthRef.current.cancel();
      }
    };
  }, []);

  // Create boundary handler for speech events
  const createBoundaryHandler = (wordOffset = 0) => {
    return (event) => {
      if (event.name === 'word' && isMountedRef.current) {
        const charIndex = event.charIndex;
        let cumulativeLength = 0;
        
        for (let i = 0; i < words.length; i++) {
          // Account for spaces between words
          const wordLength = words[i].text.length + (i === 0 ? 0 : 1);
          cumulativeLength += wordLength;
          
          if (cumulativeLength > charIndex) {
            const actualIndex = wordOffset + i;
            setCurrentIndex(actualIndex);
            if (onHighlight) onHighlight(words[i], actualIndex);
            lastSpokenIndexRef.current = actualIndex;
            break;
          }
        }
      }
    };
  };

  // Create utterance with proper events
  const createUtterance = (text, wordOffset = 0) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'en-US';
    utterance.rate = 1;
    utterance.onboundary = createBoundaryHandler(wordOffset);
    
    utterance.onend = () => {
      if (isMountedRef.current) {
        setIsPlaying(false);
        setCurrentIndex(0);
        lastSpokenIndexRef.current = 0;
      }
    };
    
    utterance.onerror = (event) => {
      console.error('SpeechSynthesisUtterance error', event);
      if (isMountedRef.current) {
        setIsPlaying(false);
      }
    };
    
    return utterance;
  };

  // Update utterance when words change
  useEffect(() => {
    if (!synthRef.current || words.length === 0) return;
    
    // Cancel any ongoing speech
    synthRef.current.cancel();
    setIsPlaying(false);
    
    // Reset to first word
    setCurrentIndex(0);
    lastSpokenIndexRef.current = 0;
    if (onHighlight && words[0]) onHighlight(words[0], 0);
    
    // Create new utterance
    const text = words.map(w => w.text).join(' ');
    const utterance = createUtterance(text, 0);
    utteranceRef.current = utterance;
    
  }, [words, onHighlight]);

  const togglePlayback = () => {
    const synth = synthRef.current;
    if (!synth) return;
    
    if (isPlaying) {
      synth.pause();
      setIsPlaying(false);
    } else {
      if (synth.paused) {
        synth.resume();
        setIsPlaying(true);
      } else {
        // Start from the beginning if we haven't spoken anything yet
        const startIndex = lastSpokenIndexRef.current > 0 ? lastSpokenIndexRef.current : 0;
        
        // Create partial utterance if needed
        const partialWords = words.slice(startIndex);
        const partialText = partialWords.map(w => w.text).join(' ');
        const utterance = createUtterance(partialText, startIndex);
        
        synth.speak(utterance);
        utteranceRef.current = utterance;
        setIsPlaying(true);
      }
    }
  };

  const handleWordClick = (word, index) => {
    const synth = synthRef.current;
    if (!synth) return;
    
    setCurrentIndex(index);
    lastSpokenIndexRef.current = index;
    if (onHighlight) onHighlight(word, index);
    
    synth.cancel();
    
    // Create partial utterance from clicked word
    const partialWords = words.slice(index);
    const partialText = partialWords.map(w => w.text).join(' ');
    const utterance = createUtterance(partialText, index);
    
    synth.speak(utterance);
    utteranceRef.current = utterance;
    setIsPlaying(true);
  };

  // Handle component unmount
  useEffect(() => {
    return () => {
      if (synthRef.current) {
        synthRef.current.cancel();
      }
      isMountedRef.current = false;
    };
  }, []);

  return (
    <div className="player-controls">
      <div className="playback-controls">
        <button 
          className="play-btn"
          onClick={togglePlayback}
          disabled={words.length === 0}
        >
          {isPlaying ? (
            <>
              <span className="icon">⏸</span> Pause
            </>
          ) : (
            <>
              <span className="icon">▶</span> Play
            </>
          )}
        </button>
        
        <div className="status-indicator">
          {isPlaying ? 'Playing...' : 'Paused'}
        </div>
      </div>

      {words.length > 0 && (
        <div className="progress-container">
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${(currentIndex / words.length) * 100}%` }}
            />
          </div>
          
          <div className="words-container">
            {words.map((word, index) => (
              <span 
                key={index}
                className={`word ${index === currentIndex ? 'active' : ''} ${index === highlightedIndex ? 'highlighted' : ''}`}
                onClick={() => handleWordClick(word, index)}
              >
                {word.text}
              </span>
            ))}
          </div>
        </div>
      )}
      
      <style jsx>{`
