// frontend/src/components/PlayerControls.js
import React, { useState, useEffect, useRef } from 'react';

const PlayerControls = ({ words, onHighlight }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [progress, setProgress] = useState(0);
  const [speakingRate, setSpeakingRate] = useState(1);
  const animationRef = useRef(null);
  const startTimeRef = useRef(null);
  const synthRef = useRef(null);
  const utteranceRef = useRef(null);
  const wordDurationsRef = useRef([]);
  const speechQueueRef = useRef([]);

  // Calculate word durations based on length and speaking rate
  const calculateWordDurations = () => {
    return words.map(word => {
      // Base duration per character (in ms)
      const baseCharDuration = 120; 
      // Additional time for word boundaries
      const wordBoundaryDuration = 200; 
      
      return Math.max(
        300, // Minimum duration for any word
        (word.text.length * baseCharDuration + wordBoundaryDuration) / speakingRate
      );
    });
  };

  // Initialize or update when words/speakingRate changes
  useEffect(() => {
    wordDurationsRef.current = calculateWordDurations();
    
    // Create speech queue
    speechQueueRef.current = words.map((word, index) => ({
      word,
      index,
      duration: wordDurationsRef.current[index]
    }));
  }, [words, speakingRate]);

  // Clean up on unmount
  useEffect(() => {
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      stopSpeech();
    };
  }, []);

  const stopSpeech = () => {
    if (synthRef.current && synthRef.current.speaking) {
      synthRef.current.cancel();
    }
    setIsPlaying(false);
    setProgress(0);
  };

  const updateHighlight = (timestamp) => {
    if (!startTimeRef.current) {
      startTimeRef.current = timestamp;
    }
    
    const elapsed = timestamp - startTimeRef.current;
    let cumulativeTime = 0;
    let currentWordIndex = 0;
    let wordProgress = 0;
    
    // Find the current word based on cumulative time
    for (let i = 0; i < speechQueueRef.current.length; i++) {
      const wordDuration = speechQueueRef.current[i].duration;
      
      if (elapsed < cumulativeTime + wordDuration) {
        currentWordIndex = i;
        wordProgress = (elapsed - cumulativeTime) / wordDuration;
        break;
      }
      
      cumulativeTime += wordDuration;
      
      // If we're at the last word and elapsed is beyond it
      if (i === speechQueueRef.current.length - 1) {
        currentWordIndex = i;
        wordProgress = 1;
      }
    }
    
    // Update state if word changed
    if (currentWordIndex !== currentIndex) {
      setCurrentIndex(currentWordIndex);
      onHighlight(speechQueueRef.current[currentWordIndex].word, currentWordIndex);
    }
    
    // Update progress (0-100)
    setProgress(Math.min(100, wordProgress * 100));
    
    // Continue animation if we're still within the speech
    if (currentWordIndex < speechQueueRef.current.length - 1 || wordProgress < 1) {
      animationRef.current = requestAnimationFrame(updateHighlight);
    } else {
      // Reached end of speech
      stopSpeech();
    }
  };

  const startPlayback = () => {
    if (!window.speechSynthesis) {
      console.warn("Web Speech API not supported");
      return;
    }
    
    stopSpeech();
    
    // Create new utterance
    const textToSpeak = words.map(w => w.text).join(' ');
    const utterance = new SpeechSynthesisUtterance(textToSpeak);
    utterance.rate = speakingRate;
    utterance.lang = 'en-US';
    
    // Setup speech events
    utterance.onstart = () => {
      synthRef.current = window.speechSynthesis;
      utteranceRef.current = utterance;
      setIsPlaying(true);
      startTimeRef.current = null;
      animationRef.current = requestAnimationFrame(updateHighlight);
    };
    
    utterance.onend = () => {
      setIsPlaying(false);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      setProgress(100);
    };
    
    utterance.onerror = (event) => {
      console.error("Speech error:", event);
      setIsPlaying(false);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      setProgress(0);
    };
    
    // Start speech
    window.speechSynthesis.speak(utterance);
  };

  const togglePlayback = () => {
    if (isPlaying) {
      stopSpeech();
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    } else {
      startPlayback();
    }
  };

  const handleWordClick = (word, index) => {
    setCurrentIndex(index);
    onHighlight(word, index);
    setProgress(0);
    
    if (isPlaying) {
      // If currently playing, restart from this word
      const newWords = words.slice(index);
      const newDurations = wordDurationsRef.current.slice(index);
      
      // Create new speech queue starting from this word
      speechQueueRef.current = newWords.map((w, i) => ({
        word: w,
        index: i,
        duration: newDurations[i]
      }));
      
      startPlayback();
    }
  };

  const handleRateChange = (newRate) => {
    setSpeakingRate(newRate);
    
    // Recalculate durations
    wordDurationsRef.current = calculateWordDurations();
    
    // Update speech queue with new durations
    speechQueueRef.current = speechQueueRef.current.map((item, index) => ({
      ...item,
      duration: wordDurationsRef.current[index]
    }));
    
    // If currently playing, restart with new rate
    if (isPlaying) {
      startPlayback();
    }
  };

  return (
    <div className="player-controls">
      <div className="playback-controls">
        <button 
          className={`play-btn ${isPlaying ? 'playing' : ''}`}
          onClick={togglePlayback}
          disabled={words.length === 0}
        >
          {isPlaying ? '⏸ Pause' : '▶ Play'}
        </button>
        
        <div className="rate-control">
          <label>Speed:</label>
          <select 
            value={speakingRate} 
            onChange={(e) => handleRateChange(parseFloat(e.target.value))}
            disabled={isPlaying}
          >
            <option value={0.7}>0.7x</option>
            <option value={0.8}>0.8x</option>
            <option value={0.9}>0.9x</option>
            <option value={1}>1.0x</option>
            <option value={1.1}>1.1x</option>
            <option value={1.2}>1.2x</option>
          </select>
        </div>
      </div>

      {words.length > 0 && (
        <div className="progress-container">
          <div className="progress">
            {words.map((word, index) => (
              <div 
                key={index}
                className={`word-container ${index === currentIndex ? 'active' : ''}`}
                onClick={() => handleWordClick(word, index)}
              >
                <span className="word">
                  {word.text}
                </span>
                {index === currentIndex && isPlaying && (
                  <div className="word-progress">
                    <div 
                      className="progress-bar" 
                      style={{ width: `${index === currentIndex ? progress : 0}%` }}
                    ></div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PlayerControls;
